import React, { Component } from "react";
import { db } from "../firebase";
export default class ContactR extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      email: "",
      message: ""
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    alert("A name was submitted: " + this.state.value);
    event.preventDefault();
  }

  handleNameChange = event => {
    this.setState({
      name: event.target.value
    });
  };
  handleEmailChange = event => {
    this.setState({
      email: event.target.value
    });
  };
  handleMessageChange = event => {
    this.setState({
      message: event.target.value
    });
  };

  render() {
    return (
      <>
        {" "}
        <h1>Welcome</h1>
        <div className="content">
          <div className="container">
            <h1 className="brand">
              <span>Lorén</span> WD
            </h1>
            <div className="wrapper animate__animated animate__bounceInLeft">
              <div className="company-info">
                <h3>Lorén WD</h3>
                <ul className="unOrder">
                  <li className="item">
                    <i className="fa fa-road"></i> 44 random street
                  </li>
                  <li className="item">
                    <i className="fa fa-phone"></i> 333 44 333
                  </li>
                  <li className="item">
                    <i className="fa fa-envelope"></i> test@test.es
                  </li>
                </ul>
              </div>

              <div className="contact">
                <h3>Send me a message!</h3>
                import {db} from "../firebase";
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }
}
